import {defineStore} from "pinia";
import axios from "axios"
export const useMainStore= defineStore('main',{
    state: ()=>({
        planData:{}
    }),
    getters: {
        getData: state => {
            state.planData
        }
    },
    actions: {
        async fetchPlans() {
            try {
                // const data = await axios.get('https://jsonplaceholder.typicode.com/users')
                // this.planData = data.data
                this.planData={
                    card:[
                        {
                            name: "Lite",
                            description: "Perfect to get started",
                            price: {
                                month:10,
                                year:120
                            },

                        },
                        {
                            name: "Pro",
                            description: "Best for professional",
                            price: {
                                month:19,
                                year:190
                            }
                        },
                        {
                            name: "Ultimate",
                            description: "Best for professional",
                            price: {
                                month:19,
                                year:190
                            }
                        },
                    ],
                    table: [
                        {"name":"CMS", "beta":0, "types":['lite','Pro','Ultimate']},
                        {"name":"Some text", "beta":true},

                    ]
                }
                console.log(this.planData)
            }
            catch (error) {
                console.log(error)
            }
        }
    },

})
