import {defineStore} from "pinia";
// import axios from "axios"
export const useMainStore= defineStore('main',{
    state: ()=>({
        planData:{}
    }),
    getters: {
        getCards: state => {
          return   state.planData?.cards
        }
    },
    actions: {
        async fetchPlans() {
            try {
                // const data = await axios.get('https://jsonplaceholder.typicode.com/users')
                // this.planData = data.data
                console.log()
                this.planData={
                    cards:[
                        {
                            type: "Lite",
                            description: "Perfect to get started",
                            price: {
                                month:10,
                                year:120
                            },

                        },
                        {
                            type: "Pro",
                            description: "Best for professional",
                            price: {
                                month:19,
                                year:190
                            }
                        },
                        {
                            type: "Ultimate",
                            description: "Toolset for hard players",
                            price: {
                                month:30,
                                year:300
                            }
                        },
                    ],
                    table: [
                        {name:'CMS integration', show:'Lite', beta:false, includes:{
                            lite:true,
                                pro:true,
                                ultimate:true
                            }},

                        {name:'Advanced analytics', show:'Pro', beta:false, includes:{
                                lite:false,
                                pro:true,
                                ultimate:true
                            }},
                        {name:'Advanced analytics 2', show:'Ultimate', beta:false, includes:{
                                lite:false,
                                pro:false,
                                ultimate:true
                            }},
                    ]
                }
                console.log(this.planData)
            }
            catch (error) {
                console.log(error)
            }
        }
    },

})
