import {defineStore} from "pinia";
import axios from "axios"
export const useMainStore= defineStore('main',{
    state: ()=>({
        planData:{}
    }),
    getters: {
        getData: state => {
            state.planData
        }
    },
    actions: {
        async fetchPlans() {
            try {
                // const data = await axios.get('https://jsonplaceholder.typicode.com/users')
                // this.planData = data.data
                this.planData={
                    card:[
                        {
                            name: "lite",
                            description: "Perfect to get started",
                            price: {
                                month:10,
                                year:120
                            },

                        },
                        {
                            name: "pro",
                            description: "Best for professional",
                            price: {
                                month:19,
                                year:190
                            }
                        },
                        {
                            name: "ultimate",
                            description: "Toolset for hard players",
                            price: {
                                month:19,
                                year:190
                            }
                        },
                    ],
                    table: [
                        {name:'CMS integration', beta:false, types:['lite']},
                        {name:'Email $ SMS reminders', beta:false, types:['lite']},
                        {name:'Customer support 24/7', beta:false, types:['lite']}
                    ]
                }
                console.log(this.planData)
            }
            catch (error) {
                console.log(error)
            }
        }
    },

})
