                        // {name:'Customer support 24/7', beta:false, types:['lite','pro']},
                        {name:'CMS integration', beta:false, types:{
                            lite:true,
                                pro:true
                            }},
                        {name:'CMS integration PRO', beta:false, types:{
                                lite:true,
                                pro:false
                            }},
                        {name:'CMS integration', beta:false, types:{
                                lite:true,
                                pro:true
                            }},
                        {name:'CMS integration Lite', beta:false, types:{
                                lite:true,
                            }}
                        // {name:'Advanced analytics', beta:false, types:['pro']},
                        // {name:'100+ integrations', beta:false, types:['pro']},
                        // {name:'Chat widget', beta:false, types:['pro']},
                        // {name:'Template library', beta:false, types:['pro']}
                    ]
                }
                console.log(this.planData)
            }
            catch (error) {
                console.log(error)
            }
        }
    },

})
